import React from 'react';

export class BookCard extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        var book = this.props;
        var isAddedToInventory = book.noOfCopies != 0;
        var cardFooter = generateCardFooter(isAddedToInventory);
        return(
            <div class="card_wrapper">
                <div id="card_body">
                    <div id="book_info">
                        <h3 id="header_container">{book.bookTitle}</h3>
                        <h4>Authors:</h4>
                        {/* <ul>{book.authors.map(index, author => {
                            <li key={index}>{author}</li>
                        })}
                        </ul> */}
                        {book.authors}
                        <h4>Published Date:</h4>{book.publishedDate}
                    </div>
                    {isAddedToInventory && <div id="inventory_status">
                        <h4>Inventory Copies</h4>
                        <button>-</button><input placeholder={book.noOfCopies} /><button>+</button>
                    </div>}
                </div>
                {cardFooter}
            </div>
        )
    }

}

generateCardFooter = (isAddedToInventory) => {
    if(isAddedToInventory) {
        return (
            <div id="card_footer">
                <button> Save </button>
                <button> Out Of Stock </button>   
            </div>
        )
    } else return (
        <div id="card_footer">
            <button> Add to Inventory </button>
            <p> No copies currently in the Inventory </p>   
        </div>
    )
}
