import React from 'react';
import { BookCard } from './BookCard';

const url = "https://book-store-service.herokuapp.com/api/book/"

export class ListBookCards extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            bookId: ''
        }
    }

    renderList = ({dataList}) => {
        if(dataList){
            return dataList.map(book => {
                if (book.bookId == this.state.bookId) {
                    book.noOfCopies = 0;
                }
                book.removeFromInventory = this.removeCardItem;
                return(<BookCard key={book.bookId} {...book} />)
            })
        }
    }

    removeCardItem =(bookId) => {
        fetch(url.concat(bookId), {
            Method: 'DELETE'}
        ).then((response) => response.json())
          .then(this.setState({bookId: bookId}));
    }

    render() {
        return this.renderList(this.props);
    }
}
